import React, { useState } from "react";
import Game from "./containers/GameContainer"
import Keyboard from "./containers/KeyboardContainer"

function Square({ value }) {
  return (
    <input 
        className="square"
        type="text"
        value={value}
        maxLength={1}
        onChange={(e) => (e.target.value)}
         />
  )
} 

function Row() {
  const [squares] = useState(Array(5).fill(''));
  
  // const row = squares.map((square, index) =>
  //   <Square key={index} value={index}/>
  // )
  const row = Array.from({ length: 5 }, (square, index) => ( 
    <Square key={index} value={square[index]}/>
  ));

  return (
    <div className="board-row">
      {row}

      {/* <Square value={squares[0]} />
      <Square value={squares[1]} />
      <Square value={squares[2]} />
      <Square value={squares[3]} />
      <Square value={squares[4]} /> */}

    </div>
  )
}

function Board() {
  const [rows] = useState(Array(6).fill(''));

  const board = rows.map((row, index) => 
    <Row key={index} />
  )

  return (
    <div className="game-board">
      {board}
    </div>
  )
}

export default function App() {

  return (
    <>
      <Board />
    </>
  );
}
